drop database incaa_db_omcn;
CREATE DATABASE incaa_db_omcn;

USE incaa_db_omcn;

CREATE TABLE peliculas (
    id_pelicula INT AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    estreno DATE,
    duracion VARCHAR(50),
    sinopsis TEXT,
    image VARCHAR(255),
    elenco TEXT,
    director VARCHAR(255),
    trailer VARCHAR(255),
    wikilink VARCHAR(255)
);

CREATE TABLE actores (
    id_actor INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    wikilink VARCHAR(255),
    image VARCHAR(255)
);


CREATE TABLE actrices (
    id_actriz INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    wikilink VARCHAR(255),
    image VARCHAR(255)
);


CREATE TABLE directores (
    id_director INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    wikilink VARCHAR(255),
    image VARCHAR(255)
);







